import React from 'react'
import profilepicture from "../uploads/profilepicture.png"
import ModeEditIcon from '@mui/icons-material/ModeEdit';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { selectUser } from '../features/userSlice';
import { getCartItems } from '../features/cartItemsSlice';
import { useState,useEffect } from 'react';
import Axios from 'axios';
import Navigationbar from './Navigationbar';
import { Link } from 'react-router-dom';
import "../css/Cart.css"
import {rooturl} from '../config/settings';
import Pagination from './Pagination';

function Orders() {
    const navigate=useNavigate();
    const url= 'http://'+rooturl+':3333'
    const user=useSelector(selectUser);
    const userid=user.id;
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(false);
    const [currentpage, setCurrentpage] = useState(1);
    const [postsPerPage, setPostsPerPage] = useState(5);
    useEffect(() => {
    getOrders();



  }, []);

    const getOrders=()=>{
      Axios.get(`${url}/getOrders/${userid}` ).
      then((response) => {
      console.log(response);
      if (response.data.success) {

          setProducts([...products, ...response.data.result]);
          console.log(products);
        } 


       else {
        console.log("Failed in ");
      }
    });
    }

      const indexOfLastPost=currentpage*postsPerPage;
      const indexOfFirstPost=indexOfLastPost-postsPerPage;
      const currentProducts=products.slice(indexOfFirstPost,indexOfLastPost)
      const paginate = pageNumber => setCurrentpage(pageNumber);
      const renderCards = products.map((product) => {

        return (

            <div className="cartitem">

            <div className="cartitem__image">


                  <img style={{width:"200px",height:"158px"}} src={require("../uploads/"+product.oproduct_id.image)} alt="" />
                </div>
          <Link to={`/ItemOverview/${product.oproduct_id._id}`} className="cartItem__name">
            <p>{product.oproduct_id.productname}</p>
          </Link>
          <p className="cartitem__price">${product.oproduct_id.price}</p>
          <p className='quantity'>{product.oquantity}</p>
          <p className='ordered_date'>{product.odate}</p>
          <br>
          </br>
          <p>{product.checked}</p>


        </div>
      );
      });


      return (
        <div>

          <Navigationbar/>
          <hr></hr>

            <h2>Your Orders</h2>


            <div style={{backgroundColor:"rgb(243, 234, 223)"}}>
              <div style={{ width: "75%", margin: "3rem auto" }}>
                <div style={{ textAlign: "center" }}></div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "flex-end",
                    margin: "1rem auto",
                  }}
                ></div>
                {products.length === 0 ? (
                  <div
                    style={{
                      display: "flex",
                      height: "300px",
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <h2>No post yet...</h2>
                  </div>
                ) : (

                  <div className="container-fluid mx-1">
                    <div className="row mt-5 mx-1">
                      <div className="col-md-15">
                        <div className="row">{renderCards}</div>

                      </div>
                    </div>

                  </div>

                )}

                <br />
                <br />
                <Pagination
        postsPerPage={currentProducts}
        totalPosts={products.length}
        paginate={paginate}
      />
             </div>

            </div>
        </div>
  )
}

export default Orders
