import React, {useEffect, useState} from "react";
import Logo from "../Images/etsylogo.png"
import "../css/Home.css";
//import Product from "./Product";
import FavoriteTwoToneIcon  from '@material-ui/icons//FavoriteBorderOutlined';
import Navigationbar from "./Navigationbar";
import Axios  from 'axios';
import { Col, Card, Row} from 'antd';
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import { selectUser } from "../features/userSlice";
/* eslint-disable import/first */
const {Meta}=Card
import {rooturl} from '../config/settings';
const token=localStorage.getItem("jwtToken");
const authAxios=Axios.create({
  baseURL:rooturl,
  headers:{
    Authorization:token
  }
})
function Home(){
   const user=useSelector(selectUser)
   const url= 'http://'+rooturl+':4000'
    const [Products, setProducts]=useState([])
    const [Skip, setSkip] = useState(0)
    const [Limit, setLimit] = useState(9)
    const [PostSize, setPostSize] = useState()
    const [SearchTerms, setSearchTerms] = useState("")



    useEffect(()=>{

    },[])
    const {error, loading, data}= useQuery(PRODUCTS, {
      variables:  {ouser_id},
        });



      if(loading){
        return<p>loading</p>

      }
      //setProducts(data.orders);
        console.log(data);
    useEffect(() => {
      const variables ={
        skip:Skip,
        limit:Limit,
      }
      getProducts(variables)
    },[])
    // Axios.defaults.withCredentials = true;
    // Axios.defaults.headers.common['Authorization'] = 'Bearer ' + localStorage.getItem('jwtToken');
    const getProducts=(variables) =>{
      authAxios.post(`${url}/getProducts`,variables)
      .then((response) =>{
        if(response.data.success){
          if (variables.loadMore) {
            setProducts([...Products, ...response.data.result])
            console.log(Products);
        } else {
            setProducts(response.data.result)
        }
        setPostSize(response.data.postSize)     
        }else{
          alert("failed to fetch products")
        }
      })
    }

    const handlefavclick=(productid,userid)=>{
      console.log(user.id)
      Axios.post(`${url}/addFav/${productid}/${userid}`)
      .then((response)=>{
        if(response.data.success){
        console.log(response.data.success)
        }
        else{
          console.log("error")
        }

      }).catch((err)=>{
        console.log(err);
      })

  }

    const renderCards = Products.map((product, index) => {

      return (<div className="col-md-4 mb-4">
                <div className="card" style={{marginTop:"20px"}}>
                  <Link to={`/ItemOverview/${product._id}`}><img style={{height:"200px",width:"200px"}}
                    src={product.image}
                    className="card-img-top-center"
                    alt="..."
                  /></Link>
                 <div
            style={{
              backgroundColor: "white",
              borderRadius: "50%",
              padding: "5px",
            }}
            className="favourite_icon"
            onClick={() => {
              handlefavclick(product._id, user.id);
            }}
          >
            {/* {toggleFavourites} */}
            <FavoriteTwoToneIcon />
            {/* {favourites.itemId === products.itemId &&
              favourites.userId === user.id} */}
          </div>
                <div className="card-body">
                  <h5 className="card-title">{product.productname}</h5>
                    <p>Price: ${product.price}</p>
                    </div>
            </div>
      </div>
         )
  })
  const onLoadMore = () => {
    let skip = Skip + Limit;

    const variables = {
        skip: skip,
        limit: Limit,
        loadMore: true,

        searchTerm: SearchTerms
    }
    getProducts(variables)
    setSkip(skip)
  }
    const updateSearchTerms = (newSearchTerm) => {

      const variables = {
          skip: 0,
          limit: Limit,

          searchTerm: newSearchTerm
      }

      setSkip(0)
      setSearchTerms(newSearchTerm)

      getProducts(variables)
  }

    return (
      <div>
        <Navigationbar/>
         <h1 style={{display: 'flex', height: '100px', justifyContent: 'center', alignItems: 'center',color:"hsl(18, 85%, 55%)"}}>Welcome back {user.name}</h1>
            <div style={{ width: '75%', margin: '1rem auto', backgroundColor:"rgb(243, 234, 223)" }}>



            {Products.length === 0 ?
                <div style={{ display: 'flex', height: '300px', justifyContent: 'center', alignItems: 'center' }}>
                    <h2>No post yet...</h2>
                </div> :
                <div className="container-fluid mx-1">
                <div className="row mt-5 mx-1">
                  <div className="col-md-15">
                    <div className="row">{renderCards}</div>
                  </div>
                </div>
              </div>
              } 
              <br /><br />
              {PostSize >= Limit &&
                <div style={{ display: 'flex', justifyContent: 'center' }}>
                    <button onClick={onLoadMore}>Load More</button>
                </div>
            }


      </div>
      </div>
    );
}

export default Home
