import React from 'react'
import profilepicture from "../uploads/profilepicture.png"
import ModeEditIcon from '@mui/icons-material/ModeEdit';
import DeleteIcon from '@mui/icons-material/Delete';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { selectUser} from '../features/userSlice';
import { getCartItems ,createFinalCart,getFinalCart,removeCartItem} from '../features/cartItemsSlice';
import { useState,useEffect } from 'react';
import Axios from 'axios';
import Navigationbar from './Navigationbar';
import { Link } from 'react-router-dom';
import "../css/Cart.css"
import { useDispatch } from 'react-redux';
import {rooturl} from '../config/settings';

function Carts() {
    const navigate=useNavigate();
    const user=useSelector(selectUser);
    //const products=useSelector(getCartItems);
    const userid=user.id;
    const [quantity,setQuantity]=useState(0)
    const dispatch=useDispatch();
    const finalCartProducts = useSelector(getCartItems);
    const url= 'http://'+rooturl+':3333'
    const [products, setProducts] = useState([]);
    const [checked, setChecked] = useState(false);
    useEffect(() => {
    getCartProducts();



  }, []);
  const getCartProducts=()=>{
    Axios.get(`${url}/getFinalCartProducts/${userid}` ).
    then((response) => {
    console.log(response);
    if (response.data.success) {

        setProducts([...products, ...response.data.result]);
        console.log(products);
      } 


     else {
      console.log("Failed in ");
    }
  });
  }
  const getQtyFromCart = (productid) => {

    Axios.get(`${url}/getQty/${userid}/${productid}`).then(
      (response) => {

        if (response.data.success) {
          console.log(response);
          var qty= response.data.result[0].quantity;
         return qty;
        } else {
          console.log("Failed in ");
        }
      }
    );
  };

  const qtyChangeHandler = (productcart, quantityupdate) => {
    console.log(productcart)
    dispatch(
      createFinalCart({
        itemId: productcart._id,
        itemName: productcart.productname,
        itemDescription: productcart.description,
        itemImage: productcart.image,
        itemPrice: productcart.price,
        itemCount: productcart.count,
        // itemId: item.itemId,
        qty: Number(quantityupdate),
        subtotal:Number(productcart.price)*Number(quantityupdate)
      })

    );
    Axios.put(`${url}/updateCartQuantity/` + user.id, {
      itemId: productcart._id,
      qty: quantityupdate,
    }).then((response) => {
      if (response.data.success === true) {
        console.log("Cart quantity updated");
      }
    });
    window.location.reload(true);
  };
  const removeHandler=(id)=>{
    Axios.post(`${url}/delCart/${userid}/${id}`).then(
      (response) => {

        if (response.data.success) {
          console.log(response);
          var qty= response.data.result[0].quantity;
         return qty;
        } else {
          console.log("Failed in ");
        }
      }
    );

    dispatch(removeCartItem(id));


  }
  if(checked==false){
    if (user && product !== null ) {


      searchPage = product.map((pro) => {

        return (
          <div className="col-md-4 mb-4" style={{marginTop:"20px"}}>
            <div className="card">
              <Link to={`/ItemOverview/${pro._id}`}><img
                src={require("../uploads/" + pro.image)}
                className="card-img-top"
                alt="..."
              /></Link>
              <div className="card-body">
                <h5 className="card-title">{pro.productname}</h5>
                <p>Price: ${pro.price}</p>
                {/* <p>{pro.itemCount}</p> */}

              </div>
            </div>
          </div>
        );
      });
    } else {
      dispatch(removeProductsState());
      searchPage = (
        <div>
          <h4> No Items</h4>
        </div>
      );
    }
  }else{
      if (user && product !== null ) {


          searchPage = product.map((pro) => {
          if(pro.count>0){ 
            return (
              <div className="col-md-4 mb-4" style={{marginTop:"20px"}}>
                <div className="card" >
                  <img
                    src={require("../uploads/" + pro.image)}
                    className="card-img-top"
                    alt="..."
                  />
                  <div className="card-body">
                    <h5 className="card-title">{pro.productname}</h5>
                    <p>Price: ${pro.price}</p>
                    {/* <p>{pro.itemCount}</p> */}

                  </div>
                </div>
              </div>
            );}
          });
        } else {
          dispatch(removeProductsState());
          searchPage = (
            <div>
              <h4> No Items</h4>
            </div>
          );
        }

  }



  const renderCards = products.map((product) => {



    return (

        <div className="cartitem">

        <div className="cartitem__image">


              <img style={{width:"200px",height:"158px"}} src={require("../uploads/"+product.cproduct_id.image)} alt="" />
            </div>
      <Link to={`/ItemOverview/${product.cproduct_id._id}`} className="cartItem__name">
        <p>{product.cproduct_id.productname}</p>
      </Link>
      <p className="cartitem__price">${product.cproduct_id.price}</p>

      <select
        defaultValue={product.quantity}

        onChange={(e) => qtyChangeHandler(product, e.target.value)}
        className="cartItem__select"
      >
        {[...Array(Number(product.cproduct_id.count)).keys()].map((x) => (
          <option key={x + 1} value={x + 1}>
            {x + 1}
          </option>
        ))}
      </select>
      <DeleteIcon

        onClick={() => removeHandler(product.cproduct_id._id)}
      >

      </DeleteIcon>
    </div>
  );
  });
  const OrderHandler=()=>{
    Axios.post(`${url}/deletefullCart/${userid}`)
    .then((response)=>{
      console.log(response);
    }).catch((err)=>{
      console.log(err);
    })

    products.map((product) =>{
      Axios.post(`${url}/editCount/${product.cproduct_id._id}`,{quantity:product.quantity})
      .then((response)=>{
        console.log(response);
      }).catch((err)=>{
        console.log(err);
      })
    })
    products.map((product)=>{
      Axios.post(`${url}/updateOrders/${userid}`,{itemId:product.cproduct_id._id,subtotal:getCartSubTotal(),quantity:product.quantity})
      .then((response)=>{
        console.log(response);

      })
    });
    navigate("/Orders")
  }
  const getCartSubTotal=()=>{



    return finalCartProducts
    .reduce((price, item) => price + item.subtotal, 0)
    .toFixed(2);
  }


  return (
    <div>

      <Navigationbar/>
      <hr></hr>

        <h2>Shopping Cart</h2>


        <div style={{backgroundColor:"rgb(243, 234, 223)"}}>
          <div style={{ width: "50%", margin: "3rem auto" }}>
            <div style={{ textAlign: "center" }}></div>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-end",
                margin: "1rem auto",
              }}
            ></div>
            {products.length === 0 ? (
              <div
                style={{
                  display: "flex",
                  height: "300px",
                  justifyContent: "center",
                  alignItems: "center",
                }}
              >
                <h2>No post yet...</h2>
              </div>
            ) : (

              <div className="container-fluid mx-1" >
                <div className="row mt-5 mx-1">
                  <div className="col-md-15">
                    <div style={{float:'left',marginLeft:"-10px"}}>{renderCards}</div>
                  </div>
                   <div>
                     <p>Total : {getCartSubTotal()}</p>
                   </div>
                   <button style={{width:"200px",borderRadius:"20px",color:"white",backgroundColor:"black"}} onClick={OrderHandler}>Proceed to checkout</button>
                </div>
              </div>

            )}

            <br />
            <br />

         </div>
        </div>
    </div>
  );
}
export default Carts
